BEGIN TRANSACTION;
CREATE TABLE "admins" ("id" SERIAL PRIMARY KEY NOT NULL, "email" varchar(255) DEFAULT '' NOT NULL, "encrypted_password" varchar(255) DEFAULT '' NOT NULL, "reset_password_token" varchar(255), "reset_password_sent_at" timestamp, "remember_created_at" timestamp, "sign_in_count" integer DEFAULT 0, "current_sign_in_at" timestamp, "last_sign_in_at" timestamp, "current_sign_in_ip" varchar(255), "last_sign_in_ip" varchar(255), "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "first_name" varchar(255), "last_name" varchar(255), "address_1" varchar(255), "address_2" varchar(255), "town" varchar(255), "county" varchar(255), "postcode" varchar(255), "contact_number" varchar(255));
CREATE TABLE "funerals" ("id" SERIAL PRIMARY KEY NOT NULL, "funeral_pref" varchar(255), "casket_pref" text, "service_location" varchar(255), "burial_location" varchar(255), "ashes_location" varchar(255), "hymns_pref" text, "readings" text, "speakers" text, "final_words" text, "user_id" integer, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE "users" ("id" SERIAL PRIMARY KEY NOT NULL, "first_name" varchar(255), "middle_names" varchar(255), "last_name" varchar(255), "date_of_birth" timestamp, "gender" varchar(255), "email" varchar(255), "address_1" varchar(255), "address_2" varchar(255), "town" varchar(255), "county" varchar(255), "postcode" varchar(255), "contact_number" varchar(255), "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "password_hash" varchar(255), "encrypted_password" varchar(255) DEFAULT '' NOT NULL, "reset_password_token" varchar(255), "reset_password_sent_at" timestamp, "remember_created_at" timestamp, "sign_in_count" integer DEFAULT 0, "current_sign_in_at" timestamp, "last_sign_in_at" timestamp, "current_sign_in_ip" varchar(255), "last_sign_in_ip" varchar(255), "confirmation_token" varchar(255), "confirmed_at" timestamp, "confirmation_sent_at" timestamp, "unconfirmed_email" varchar(255), "admin" boolean);
CREATE UNIQUE INDEX "index_admins_on_email" ON "admins" ("email");
CREATE UNIQUE INDEX "index_admins_on_reset_password_token" ON "admins" ("reset_password_token");
CREATE UNIQUE INDEX "index_users_on_email" ON "users" ("email");
CREATE UNIQUE INDEX "index_users_on_reset_password_token" ON "users" ("reset_password_token");
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
COMMIT;

